import React, { useState } from 'react';
import ExpenseItem from './ExpenseItem'
import Card from '../UI/Card'
import ExpenseFilter from './ExpenseFilter'
import './Expenses.css'

function Expenses(props) {
    const [filteredData, setFilteredData] = useState(props.expenses)

    const ExpenseFilterHandler = (filterYear) => {
        setFilteredData(previousExpenseData =>
            props.expenses.filter(expenses => {
                return expenses.date.getFullYear().toString() === filterYear
            })
        )
    }

    return (
        <div>
            <Card className="expenses">
                <ExpenseFilter onExpenseFilter={ExpenseFilterHandler}></ExpenseFilter>
                {filteredData.map((expense) => {
                    return (<ExpenseItem
                        key={expense.id}
                        date={expense.date}
                        title={expense.title}
                        amount={expense.amount} />
                    )
                })
                }
            </Card>
        </div>
    )
}

export default Expenses;
