import  {useState} from 'react'
import Expenses from './components/Expenses/Expenses'
import AddExpense from './components/NewExpense/AddExpense'
import './App.css';

const DUMMY_EXPENSES = [
  {
    id: 'e1',
    title: 'Toilet Paper',
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: 'e3',
    title: 'Car Insurance',
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: 'e4',
    title: 'New Desk (Wooden)',
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

function App() {
  const [previousExpense, setExpenses] = useState(DUMMY_EXPENSES)

  const AddExpenseHandler = (newExpenseData) =>{
    setExpenses((previousExpense) => [...previousExpense, newExpenseData])
  }
  return (
    <div>
      <AddExpense onAddExpense={AddExpenseHandler}/>
      <Expenses expenses={previousExpense} />
    </div>
  );
}

export default App;
